// --- Preloader (mimicking ambiq.ai) ---
.preloader {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: $jennai-neutral-dark;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    z-index: 9999;
    opacity: 1;
    visibility: visible;
    transition: opacity 0.5s ease, visibility 0.5s ease;

    &.hidden {
        opacity: 0;
        visibility: hidden;
    }

    .lines div {
        width: 4px;
        height: 30px;
        background-color: $jennai-primary-comp;
        margin: 0 2px;
        animation: loading-line 1.2s infinite ease-in-out;
        display: inline-block;
    }
    .lines div:nth-child(2) { animation-delay: 0.1s; }
    .lines div:nth-child(3) { animation-delay: 0.2s; }
    .lines div:nth-child(4) { animation-delay: 0.3s; }
    .lines div:nth-child(5) { animation-delay: 0.4s; }
    .lines div:nth-child(6) { animation-delay: 0.5s; }

    .text {
        font-family: $font-heading;
        font-size: 2em;
        color: white;
        margin-top: 20px;
        span {
            position: relative;
            &::before {
                content: attr(data-newtext);
                position: absolute;
                top: 0;
                left: 0;
                color: $jennai-secondary-comp;
                width: 0;
                overflow: hidden;
                transition: width 0.5s ease-in-out;
            }
        }
    }
}

@keyframes loading-line {
    0%, 100% { transform: scaleY(1); }
    50% { transform: scaleY(0.3); }
}

// --- Header ---
header {
    background-color: $theme-primary;
    color: $theme-font;
    padding: 1em 2em;
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: fixed;
    width: 100%;
    z-index: 100;
    box-shadow: 0 2px 5px rgba($jennai-neutral-dark, 0.1);

    .site-logo img {
        height: $header-height-desktop;
        display: block;
        transition: height 0.3s ease;
    }

    .main-navigation ul {
        list-style: none;
        margin: 0;
        padding: 0;
        display: flex;
    }
    .main-navigation li {
        margin-left: 20px;
    }
    .main-navigation a {
        color: $theme-font;
        font-weight: 600;
        &:hover { color: $theme-accent-1; }
    }

    .desktop-burger, .mobile-burger { display: none; }
    @media (max-width: 768px) {
        .main-navigation { display: none; }
        .mobile-burger { display: block; }
        .site-logo img { height: $header-height-mobile; }
    }
}

// --- Side Navigation ---
aside.side-nav {
    position: fixed;
    right: 2em;
    top: 50%;
    transform: translateY(-50%);
    z-index: 99;
    display: flex;
    flex-direction: column;
    align-items: center;

    .nums div {
        font-family: $font-heading;
        font-size: 1.2em;
        color: $theme-font;
        margin: 0.5em 0;
    }
    .nums .line {
        width: 1px;
        height: 30px;
        background-color: lighten($theme-font, 60%);
        margin: 0.5em 0;
    }
    .arrows button {
        background: none;
        border: none;
        color: $theme-font;
        font-size: 1.5em;
        cursor: pointer;
        padding: 5px;
        &:hover { color: $theme-accent-1; }
    }
}

// --- Main Content Sections ---
main {
    padding-top: $header-height-desktop;
    section {
        min-height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        padding: $padding-section;
        position: relative;
        overflow: hidden;
        flex-direction: column;
    }

    .section-content {
        display: flex;
        width: 100%;
        max-width: $max-width-content;
        margin: 0 auto;
        justify-content: space-between;
        align-items: center;
        gap: 2em;
        flex-wrap: wrap;
    }

    // --- Section 1: Welcome / Hero ---
    .welcome-section {
        background-color: $theme-background;
        color: $theme-font;
        min-height: 100vh;
        position: relative;

        .section-content {
            flex-direction: row;
            align-items: flex-start;
            @media (max-width: 768px) { flex-direction: column; }
        }
        .text-column {
            flex: 1;
            padding-right: 2em;
            @media (max-width: 768px) { padding-right: 0; margin-bottom: 2em; }
        }
        .visual-column {
            flex: 1;
            display: flex;
            justify-content: flex-end;
            .hero-visual {
                max-width: 100%;
                height: auto;
                border-radius: 10px;
                box-shadow: 0 5px 15px rgba($jennai-neutral-dark, 0.2);
            }
        }
        .section-title { font-size: 3.5em; color: $theme-intermediate-dark-red; line-height: 1.1; }
        .section-description { font-size: 1.2em; max-width: 80%; }
    }

    // --- Section 2: Core Mission ---
    .mission-section {
        background-color: lighten($theme-background, 5%);
        color: $theme-font;
        .section-content {
            flex-direction: row;
            @media (max-width: 768px) { flex-direction: column-reverse; }
        }
        .mission-text {
            flex: 1;
            padding-right: 2em;
            .section-subtitle { font-size: 2.2em; color: $jennai-primary-comp; }
            .mission-statement-text {
                font-family: $font-heading;
                font-size: 2.5em;
                font-weight: bold;
                color: $theme-font;
                line-height: 1.1;
            }
        }
        .mission-visual {
            flex: 0.7;
            display: flex;
            justify-content: center;
            align-items: center;
            img { max-width: 100%; height: auto; border-radius: 10px; }
        }
    }

    // --- Section 3: Vision (Expansive Explanation) ---
    .vision-section {
        background-color: $jennai-neutral-dark;
        color: white;

        .section-content {
            flex-direction: row;
            @media (max-width: 768px) { flex-direction: column; }
        }
        .section-subtitle { font-size: 2.2em; color: $jennai-primary-comp; }
        .vision-text-block {
            flex: 1.5;
            font-family: $font-body;
            font-size: 1.1em;
            line-height: 1.8;
            padding-right: 3em;

            h3 { color: $jennai-secondary-comp; font-size: 1.8em; margin-bottom: 0.8em;}
            strong { color: $jennai-secondary-comp; }
            p { margin-bottom: 1em; }
        }
        .vision-visual {
            flex: 1;
            display: flex;
            justify-content: center;
            align-items: center;
            img { max-width: 100%; height: auto; border-radius: 10px; }
        }
    }

    // --- Section 4: Call to Action ---
    .call-to-action-section {
        background-color: $theme-accent-2;
        color: $theme-font;
        text-align: center;
        min-height: auto;
        padding: 5em 2em;

        .section-content {
            flex-direction: column;
            max-width: 800px;
        }
        .section-title { color: $jennai-neutral-dark; font-size: 3em; }
        .section-description { font-size: 1.2em; margin-bottom: 2em; }
        .button.action-button {
            background-color: $jennai-secondary-comp;
            color: white;
            &:hover { background-color: darken($jennai-secondary-comp, 10%); }
        }
    }
}

// --- Video Pop-up Modal (mimicking ambiq.ai) ---
.video-pop-up {
    position: fixed;
    top: 0; left: 0;
    width: 100%; height: 100%;
    background-color: rgba($jennai-neutral-dark, 0.9);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.3s ease, visibility 0.3s ease;

    &.active { opacity: 1; visibility: visible; }

    .content {
        position: relative;
        width: 90%; max-width: 1000px;
        background: black;
        border-radius: 10px;
        overflow: hidden;
        box-shadow: 0 10px 30px rgba($jennai-neutral-dark, 0.5);
        padding-top: 56.25%;

        .video {
            position: absolute; top: 0; left: 0;
            width: 100%; height: 100%;
        }
    }
    .close {
        position: absolute; top: 15px; right: 15px;
        background: $jennai-secondary-comp;
        color: white;
        border: none; border-radius: 50%;
        width: 40px; height: 40px;
        display: flex; justify-content: center;
        align-items: center;
        cursor: pointer; font-size: 1.5em; z-index: 1001;
        &:hover { background-color: lighten($jennai-secondary-comp, 10%); }
    }
}

// --- Footer ---
footer {
    background-color: $jennai-neutral-dark;
    color: white;
    padding: 4em 2em;
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;

    .footer-column {
        flex: 1;
        min-width: 200px;
        margin: 1em;
    }
    .footer-column h4 {
        font-family: $font-heading;
        color: $jennai-primary-comp;
        margin-bottom: 1em;
    }
    .footer-column ul {
        list-style: none;
        padding: 0;
        margin: 0;
    }
    .footer-column li a {
        color: lighten($jennai-neutral-dark, 40%);
        text-decoration: none;
        &:hover { color: $jennai-secondary-comp; }
    }
    .footer-portrait {
        height: 80px;
        width: 80px;
        border-radius: 50%;
        object-fit: cover;
        margin-top: 1em;
        border: 2px solid $jennai-primary-comp;
    }
    .copyright {
        font-size: 0.9em;
        color: lighten($jennai-neutral-dark, 30%);
        margin-top: 1em;
    }
    .privacy-link {
        font-size: 0.9em;
        color: lighten($jennai-neutral-dark, 30%);
        &:hover { color: $jennai-secondary-comp; }
    }

    .bg_line {
        position: absolute;
        width: 100px;
        height: 100%;
        background: linear-gradient(to bottom, rgba($jennai-primary-comp, 0.1) 0%, transparent 100%);
        z-index: -1;
        &.l { left: 0; }
        &.c { left: 50%; transform: translateX(-50%); }
        &.r { right: 0; }
    }
}
